import './App.css';
import { useEffect, useState } from "react"
import { db } from './firebase-config';
import { collection,getDocs,addDoc, updateDoc,doc } from "firebase/firestore"
function App() {
  const [ user,setUser ] = useState([])
  const usersCollection = collection(db,"users")
  const [ newName,setNewName ] = useState("")
  const [ newAge,setNewAge ] = useState(0)
  const [ loading , setLoading ] = useState(false)
  const createUser = async()=>{
    setLoading(true)
    await addDoc(usersCollection,{name:newName,age:Number(newAge)})
    setLoading(false)
  }
  const updateUser = async(id,age)=>{
    setLoading(true)
    const userDoc = doc(db,"users",id)
    const newField = { age:Number(age) + 1 }
    await updateDoc(userDoc,newField)
    setLoading(false)

  }
  useEffect(()=>{
     const getUser = async()=>{
        const data = await getDocs(usersCollection)
        // console.log(data)
        setUser(data.docs.map((doc)=>({ ...doc.data() , id: doc.id })))
     };
     getUser();
  }, [loading])
// console.log(user)
  return (
    <div className="App">                  
      <h2>{loading? "Please Wait...":null}</h2>
      <input type="text" placeholder='Name...' onChange={(e)=>setNewName(e.target.value)} />
      <input type="number" placeholder='Age...' onChange={(e)=>setNewAge(e.target.value)} />
      <button onClick={createUser}>Create User</button>
      <div className='users'>
      {user.map((item)=>(
      <fieldset style={{width:"20%"}}>
        <legend>{item.name}</legend>
        <h4>Id : {item.id}</h4>
        <h1>Name : {item.name}</h1>
        <h1>Age : {item.age}</h1>
        <button onClick={()=>{updateUser(item.id,item.age)}}>Increase Age</button>
        <button>Delete User</button>
      </fieldset>
      ))}
      </div>
    </div>
  );
}

export default App;
