import React,{ useEffect,useState } from 'react'
import { data } from "../data.js"

export default function FiltterPage() {
  // console.log(data)
  const [ search,setSearch ] = useState("")
  // console.log(search)
  // console.log(data)
  // if(data){
  //   data.filter((item)=>{
  //     if(!search == "" && item.name.toLowerCase( ).includes(search.toLowerCase())){
  //       console.log(item) 
  //     }
  //   })
  // }
  const [ page,setPage ] = useState(0,10)
  if(data){
    console.log(data.slice(0,10))
  }
  return (
    <div className='p-5'>
      <div className="table-responsive">
        <input className='form-control my-2' onChange={(e)=>setSearch(e.target.value)} placeholder='search' />
        <table className="table table-warning table-striped table-hover">
          <thead>
            <tr>
              <th scope="col">#id</th>
              <th scope="col">Name</th>
              <th scope="col">Email</th>
              <th scope="col">Phone</th>
            </tr>
          </thead>
          <tbody>
            {data && data.filter((item)=>{
               return search.toLowerCase() === "" ? item:item.name.toLocaleLowerCase().includes(search.toLowerCase())
            }).slice(page).map((items)=>(
            <tr key={items.id} className="">
              <td scope="row">{items.id}</td>
              <td>{items.name}</td>
              <td>{items.email}</td>
              <td>{items.phone}</td>
            </tr>
            ))}
          </tbody>
          <tfoot className=''>
            <div className="btn-group my-3">
            <button className='btn' onClick={()=>setPage(0,10)}>1</button>
            <button className='btn'>2</button>
            <button className='btn'>3</button>
            <button className='btn'>4</button>
            <button className='btn'>5</button>
            <button className='btn'>6</button>
            <button className='btn'>7</button>
            <button className='btn'>8</button>
            <button className='btn'>9</button>
            <button className='btn'>10</button>
            </div>
          </tfoot>
        </table>
      </div>
      
    </div>
  )
}
